class FormValidation{constructor(t){this._form=t,this._inputList=Array.from(this._form.querySelectorAll(".form__input")),this._btnElem=document.querySelector(".form__submit")}toggleBtnState(){this._inputList.some((t=>!t.validity.valid))?this._inactiveButtonSubmit():this._activateButtonSubmit()}_inactiveButtonSubmit(){this._btnElem.setAttribute("disabled",!0),this._btnElem.classList.add("form__submit_disabled")}_activateButtonSubmit(){this._btnElem.removeAttribute("disabled",!0),this._btnElem.classList.remove("form__submit_disabled")}_setEventListeners(){this._inactiveButtonSubmit(),this._inputList.forEach((t=>{t.addEventListener("input",(()=>{this.toggleBtnState()}))}))}enableValidation(){this._form.addEventListener("submit",(t=>t.preventDefault())),this._setEventListeners()}}const form=document.querySelector(".form"),validation=new FormValidation(form);validation.enableValidation();